// lazyload
const lazyLoadInstance = new LazyLoad({ 
	elements_selector: '.lazy',
})

// make anchor links scroll smoothy
$(".sliding-link").click(function(e) {
	e.preventDefault();
	var aid = $(this).attr("href");
	$('html,body').animate({scrollTop: $(aid).offset().top},'slow');
});

// correct label click
$('label').click(function(e){
	e.stopImmediatePropagation();
});

// fancybox
window.fancybox_settings = {
	smallBtn: false,
	toolbar: "auto",
	buttons: [
		"close"
	],
	arrows: true,
	touch: false,
	loop: true,
	autoFocus: false,
	animationEffect : "zoom-in-out",
	transitionEffect : "slide",
	idleTime: false,
	helpers : { 
		overlay : {
			closeClick: true
		}
	}
};

$.extend($.fancybox.defaults, fancybox_settings);

// init wow
wow = new WOW({
	live: true
}).init();

// init rellax
var rellax = new Rellax('.rellax');

$(document).ready(function() {

	// show success message after form submit
	$(document).ready(function() {
		if (window.location.href.indexOf("contact-success") > -1) {
			$.fancybox.open($("#contact-success"));
		};
	});

	// hide items ons scroll
	$(document).on('scroll', function() {
		if ($(this).scrollTop() > 100) {
			$('.floating-whatsapp').fadeIn(900);
		} else {
			$('.floating-whatsapp').fadeOut(300);
		}
	});

	// title slider
	var title_slider = new Swiper ('.title-slider', {
		slidesPerView: 1,
		allowTouchMove: false,
		disableOnInteraction: true,
		autoHeight: true,
		calculateHeight: true,
		effect: 'fade',
		fadeEffect: {
			crossFade: true
		},
		autoplay: {
			delay: 3000,
		},
		speed: 600,
	});

	// correct services slider size on initial load
	setTimeout(function(){ 
		title_slider.updateAutoHeight(300)
	}, 200);

	// animated numbers
	var waypoint = new Waypoint({
		element: $('.purple-box'),
		handler: function(direction) {
			$('.counter').each(function () {
				$(this).prop('Counter', 0).animate({
						Counter: $(this).data('value')
					}, {
					duration: 5000,
					easing: 'swing',
					step: function (now) {                      
						$(this).text(this.Counter.toFixed(0));
					}
				});
				waypoint.disable();
			});
		},
		offset: '90%'
	});

	// mask for the phone number
	var cleave = new Cleave("input[type='tel']", {
		blocks: [0, 2, 4, 4],
		delimiters: ['(', ') ', ' - ',' '],
		numericOnly: true,
		delimiterLazyShow: true
	});

	// validate form
	$("#contact-form").validate({
		highlight: function (element){
			$(element).closest(".form-line").addClass("error");
		},
		unhighlight: function (element){
			$(element).closest(".form-line").removeClass("error");
		}
	});

});